import { useState } from 'react'
import style from './MessageCreator.module.css'
import { RiSendPlaneFill } from 'react-icons/ri'

export const MessageCreator = () => {
	const [messageText, setMessageText] = useState('')
	const handleSubmit = (e) => {
		e.preventDefault();
		setMessageText('')
	}
	return (
		<div>
			<form onSubmit={handleSubmit}>
				<div className={style.formBox}>
					<input className={style.messageInput} placeholder='Your message...' type='text' name='messageText' onChange={(e) => setMessageText(e.target.value)} value={messageText}></input>
					<button className={style.btnSendMessage} type='submit'><RiSendPlaneFill size='30px' color='#2ea6ff' /></button>
				</div>
			</form>
		</div>
	)
}
