	)
}

const MessageCreator = () => {
	const [messageText, setMessageText] = useState('')
	const handleSubmit = (e) => {
		e.preventDefault();
		setMessageText('')
	}
	return (
		<div>
			<form onSubmit={handleSubmit}>
				<div className='container'>
					<input className='inputMessage' placeholder='Your message...' type='text' name='messageText' onChange={(e) => setMessageText(e.target.value)} value={messageText}></input>
					<button className='btnSend' type='submit'><RiSendPlaneFill size='30px' color='#2ea6ff' /></button>
				</div>
			</form>
		</div>
	)
}
export default App;
