import './style.scss'
import {Formik} from 'formik'
import { Link } from 'react-router-dom'
import {SiWechat} from 'react-icons/si'

const Login = () => {
	return (
		<div className="registerContainer">
			<div className="registerWallpapper">
				<span className='registerChatName'>
					<SiWechat size='24px'/>
					MVDNXS Chat</span>
				<span>Register</span>
				<Formik
					initialValues={{
						email: '',
						password: '',
					}}
						validate={values => {
							const errors = {};
							if(!values.email){
								errors.email = 'Required';
							}else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)){
								errors.email = 'Invalid email address';
							}
							if(!values.password){
								errors.password = 'Required';
							}
							return errors
						}}
						onSubmit={(values, {setSubmitting}) => {
							setTimeout(() => {
								alert(JSON.stringify(values, null, 2));
								setSubmitting(false);
							}, 400);
						}}>
							{({
								values,
								errors,
								touched,
								handleChange,
								handleBlur,
								handleSubmit,
								isSubmitting}) => (
									<form onSubmit={handleSubmit} className='registerForm'>
										<input name='email' type="email" placeholder='Email' value={values.email} onChange={handleChange} onBlur={handleBlur}/>
										{errors.email && touched.email && errors.email}
										<input name='password' type="password" placeholder='Password' value={values.password} onChange={handleChange} onBlur={handleBlur}/>
										<button type="submit" disabled={isSubmitting}>Sign in</button>
									</form>)
							}
				</Formik>
				<span>You do have account? <Link className='link' to='/register'>Register</Link></span>
			</div>
		</div>
	)
}

export default Login
