import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import { usersAPI } from '../../api/API'

export const getUsersServer = createAsyncThunk(
	'users/fetchUserById', // отображается в dev tools и должно быть уникально у каждого Thunk
	async (userId) => {
		// Здесь только логика запроса и возврата данных
		// Никакой обработки ошибок
		const response = await axios.get(getUserUrl(userId));
		return response.data;
	}
);


const initialState = {
	entities: [],
	loading: 'idle',
}

// Then, handle actions in your reducers:
const usersSlice = createSlice({
	name: 'users',
	initialState,
	reducers: {
		// standard reducer logic, with auto-generated action types per reducer
	},
	extraReducers: (builder) => {
		// Add reducers for additional action types here, and handle loading state as needed
		builder.addCase(fetchUserById.fulfilled, (state, action) => {
			// Add user to the state array
			state.entities.push(action.payload)
		})
	},
})

export const { addCase } = usersSlice.action
export default usersSlice.reducer
// Later, dispatch the thunk as needed in the app
