import './App.css';
import { useDispatch, useSelector } from 'react-redux';
import { addItem, getUsersServer } from './redux/reduxSlices/usersSlice'
import { useEffect, useState } from 'react';

function App() {
	const users = useSelector((state) => state.users.users)
	const dispatch = useDispatch()
	let [searchValue, setSearchValue] = useState('');
	// useEffect(() => {
	// 	dispatch(getUsersServer())
	// }, [dispatch])

	const setValue = (e) => {
		setSearchValue(e.target.value)
	}
	return (
		<div className="App">
			<input type='text' name='username' placeholder='username' onChange={setValue} value={searchValue}></input>
			{users
				.filter(u => u.username.includes(searchValue))
				.map(u => <div key={u.id}>username: {u.username}</div>)}
		</div>
	);
}
export default App;
