import './App.css';
import { useSearchParams } from 'react-router-dom'
import { useEffect, useState } from 'react';
import { usersAPI } from './api/API'

function App() {

	let [state, setState] = useState('')
	const [searchParams, setSearchParams] = useSearchParams();
	const userQuery = searchParams.get('username' || '')

	let handleSubmit = (e) => {
		e.preventDefault;
		setSearchParams({ username: state })
		useEffect(() => {
			fetch('https://jsonplaceholder.typicode.com/users/' + userQuery)
				.then(res => res.json())
				.then()
		})
		console.log(userQuery)
	}
	console.log(searchParams)
	let onChange = (e) => {
		setState(e.target.value)
	}
	return (
		<div className="App">
			<form autoComplete='off' onSubmit={handleSubmit}>
				<input type='text' name='username' onChange={(e) => { onChange(e) }} value={state}></input>
			</form>

		</div>
	);
}



export default App;
