import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import { usersAPI } from '../../api/API'

export const getUsersServer = createAsyncThunk(
	'users/fetchUserById', // отображается в dev tools и должно быть уникально у каждого Thunk
	async () => {
		// Здесь только логика запроса и возврата данных
		// Никакой обработки ошибок
		const response = await usersAPI.getUsers();
		return response.data;
	}
);


const initialState = {
	users: [],
	status: null,
	error: null,
}

// Then, handle actions in your reducers:
const usersSlice = createSlice({
	name: 'users',
	initialState,
	reducers: {
		// standard reducer logic, with auto-generated action types per reducer
	},
	extraReducers: (builder) => {
		builder
			.addCase(getUsersServer.pending, (state) => {
				state.loadingStatus = 'loading';
				state.error = null;
			})
			.addCase(getUsersServer.fulfilled, (state, action) => {
				// Добавляем пользователя
				state.users = action.payload;
				state.loadingStatus = 'fulfilled';
				state.error = null;
			})
			.addCase(getUsersServer.rejected, (state, action) => {
				state.loadingStatus = 'failed';
				state.error = action.error;
			});
	},
})


export default usersSlice.reducer
// Later, dispatch the thunk as needed in the app
